use std::collections::HashSet;

pub fn first_marker(message_size: usize) -> usize {
    let mut set = HashSet::with_capacity(4);
    let chars = DATA.chars().collect::<Vec<char>>();
    for i in 0..message_size {
        set.insert(chars[i]);
    }

    if set.len() == message_size {
        return message_size;
    }

    for i in 5..chars.len() {
        set.clear();
        for j in i..(i + message_size) {
            set.insert(chars[j]);
        }

        if set.len() == message_size {
            return i + message_size;
        }
    }

    panic!("Not found");
}

const DATA: &str = "";
